var crypto = require('crypto');
var decipher = crypto.createDecipher(process.argv[2],process.argv[3]);
var concat = require('concat-stream');

var zlib = require('zlib');
var guz = zlib.createGunzip();

var tar = require('tar');
var parser = tar.Parse();
parser.on('entry', function(e) {
if(e.type !== 'File') return;
var md5 = crypto.createHash('md5' ,{encoding: 'hex'});
    e.pipe(md5).pipe(concat(function(hash){
    console.log(hash + " " + e.path);        
    }));
})


process.stdin.pipe(decipher).pipe(guz).pipe(parser);
